// App.js
import React, { useState } from 'react';
import './App.css'; // Import your CSS file

function App() {
    const [selectedGenre, setSelectedGenre] = useState('fantasy');
    const [userDescription, setUserDescription] = useState('');
    const [recommendations, setRecommendations] = useState([]);

    const handleGenerateRecommendations = () => {
      fetch('http://127.0.0.1:5000/api/books', { // Adjust the URL accordingly
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({ genre: selectedGenre, description: userDescription })
      })
      .then(response => response.json())
      .then(data => {
          setRecommendations(data);
      })
      .catch(error => console.error('Error:', error));
  };

    return (
        <div className="App">
            <header>
                <h1>Book Recommendation App</h1>
            </header>
            <main>
                <div className="container">
                    <h2>Find Your Next Book</h2>
                    <div className="input-group">
                        <div className="label-row">
                            <label htmlFor="genre">Genre:</label>
                            <select
                                id="genre"
                                className="genre-dropdown"
                                value={selectedGenre}
                                onChange={(e) => setSelectedGenre(e.target.value)}
                            >
                                <option value="fantasy">Fantasy</option>
                                <option value="mystery">Mystery</option>
                                <option value="romance">Romance</option>
                                {/* Add more genres as needed */}
                            </select>
                        </div>
                        <div className="input-group">
                            <label htmlFor="description">What are you looking for today?</label>
                            <textarea
                                id="description"
                                rows="4"
                                value={userDescription}
                                onChange={(e) => setUserDescription(e.target.value)}
                            ></textarea>
                        </div>
                        <button id="generate-btn" onClick={handleGenerateRecommendations}>Generate Recommendations</button>
                        <div id="recommendations">
                            <h3>Recommendations</h3>
                            <ul>
                                {recommendations.map((book, index) => (
                                    <li key={index}>{book}</li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </main>
            <footer>
                <p>&copy; 2024 Book Recommendation App</p>
            </footer>
        </div>
    );
}

export default App;
