// App.js
import React, { useState } from 'react';
import './App.css'; // Import your CSS file

function App() {
    const [selectedVoice, setSelectedVoice] = useState('Alloy');
    const [userDescription, setUserDescription] = useState('');
    const [playAudio, setPlayAudio] = useState(false);
    const [meditationText, setMeditationText] = useState('');

    const handleGenerateMeditation = () => {
        fetch('http://localhost:8000/api/meditation', { // Adjust the URL accordingly
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ voice: selectedVoice, description: userDescription, play_audio: playAudio })
        })
        .then(response => response.json())
        .then(data => {
            setMeditationText(data.meditationText);
        })
        .catch(error => console.error('Error:', error));
    };

    return (
        <div className="App">
            <header>
                <h1>AI Guided Meditation</h1>
            </header>
            <main>
                <div className="container">
                    <h2>Find Your Peace</h2>
                    <div className="input-group">
                        <div className="label-row">
                            <label htmlFor="voice">Voice:</label>
                            <select
                                id="voice"
                                className="voice-dropdown"
                                value={selectedVoice}
                                onChange={(e) => setSelectedVoice(e.target.value)}
                            >
                                <option value="Alloy">Alloy</option>
                                <option value="Onyx">Onyx</option>
                                <option value="Random">Random</option>
                            </select>
                        </div>
                        <div className="input-group">
                            <label htmlFor="description">What object would you like as a guru today?</label>
                            <textarea
                                id="description"
                                rows="2"
                                value={userDescription}
                                onChange={(e) => setUserDescription(e.target.value)}
                            ></textarea>
                        </div>
                        <div className="input-group">
                            <input
                                type="checkbox"
                                id="playAudio"
                                checked={playAudio}
                                onChange={(e) => setPlayAudio(e.target.checked)}
                            />
                            <label htmlFor="playAudio">Play Audio?</label>
                        </div>
                        <button id="generate-btn" onClick={handleGenerateMeditation}>Generate Meditation</button>
                        <div id="meditation-text">
                            <h3>Guided Meditation</h3>
                            <p>{meditationText}</p>
                        </div>
                    </div>
                </div>
            </main>
            <footer>
                <p>&copy; 2024 AI Guided Meditation</p>
            </footer>
        </div>
    );
}

export default App;
